INSERT INTO `love_letter` VALUES ('1200000000001280012', '不专心吃饭的人', '牛仔外套女孩', '10.18周四晚六点左右在男单二楼看见你 扎着辫子，深蓝带帽子的牛仔外套，很简单的外形给人感觉很舒服很好看，表白一下这样的妹子', '120000000000128', '2b74c61d2ce0e87923220234f3619b7d');
