INSERT INTO `love_letter` VALUES ('1200000000001300017', '一个被你迷住了的女孩', '18工管二班jyh', '从九月八号开学到现在也快两个月了，看你的第一眼就喜欢上你了，上课时你总喜欢带着你的滑板，那么下次，我希望你能带上我。', '120000000000130', '2b74c61d2ce0e87923220234f3619b7d');
