INSERT INTO `love_letter` VALUES ('1200000000001310023', '17网络系小师妹', '网络系跳高的第一名男生', '还记得那天网络系系运会上，阳光正好，你在跳高，而我坐在草地上看着你。我深深地被你所吸引，你那完美的跳跃弧线至今留在我的脑海中挥之不去，请问你有对象吗？', '120000000000131', '2b74c61d2ce0e87923220234f3619b7d');
