INSERT INTO `love_letter` VALUES ('1200000000001320048', '上期27小哥哥', '陈研（妍）艺', '就练车时见了你一次，然后脑子里都是你，看到穿和你同款衣裤的都以为是你，可惜都不是，心里总会有种失落感。什么时候能再见到你？如果再见到，我会鼓起勇气去问你要微信，不想再错过。', '120000000000132', '2b74c61d2ce0e87923220234f3619b7d');
