INSERT INTO `love_letter` VALUES ('1200000000001330010', '惠', '上期132期表白墙的一个路过的小学弟', '（1356005590*微信号，后一位数字隐藏）看了132期的留言仿佛回到了大一大二，即将毕业，留个言，希望在表白墙留下大学的足迹。对师弟说的一句话，对所有师弟说的一句话！你不勇敢，师姐还是师姐！（PS有个老师姐有男朋友了，有个还单身）当朋友转给我们俩“小姐姐”你的留言时，“图书馆207？”“出双入对？”俩老师姐顿时热泪盈眶，谢谢你的抬爱哈哈哈哈哈哈。', '120000000000133', '2b74c61d2ce0e87923220234f3619b7d');
