INSERT INTO `love_letter` VALUES ('1200000000001350037', '你的猪', '我的大宝贝', '从遇见你开始，我觉得你就是我的缘分，只要和你在一起，每一天都是开心的，无论什么话都想和你说，对你倾诉，你是我生活中一部分，我们都不需要掩饰可以在对方面前做最真实的自己，即使很幼稚又怎样，那些琐碎日常里的孩子气，都是我爱你最赤诚的真心啊。', '120000000000135', '2b74c61d2ce0e87923220234f3619b7d');
