INSERT INTO `love_letter` VALUES ('1200100201608090048', '小鱼', '梁先生', 'Boss,原来这里才是华软的表白墙，你大概也不会看到。在心里，在梦里，在你在与不在的日子里。不能成为你的牵绊，浏览有关于华软的，想象你在华软的日子。岁月静好，今年的七夕是你生日，生日快乐。', '120010020160809', '2b74c61d2ce0e87923220234f3619b7d');
