INSERT INTO `love_letter` VALUES ('1200100201802140075', '爱你的那个男孩?', '陈思锐', '却妄图用三年的时间去忘记你！又是一年情人节，我想你了用六年的时间去爱你，去思念你！又怎么可能呢？你早已驻扎在我心里了！你还好吗？你呢？锐我永远都忘不了你o', '120010020180214', '2b74c61d2ce0e87923220234f3619b7d');
INSERT INTO `love_letter` VALUES ('1200100201802140076', '鹿', '倩倩小宝宝', '在一起快三年了，每一次的情人节貌似都没有陪在我小宝宝身边，不是很开心，以后的情人节一定要好好补偿回给我宝宝，带我的小宝宝吃吃吃，喝喝喝，浪浪浪，只要能让我宝宝开心。我花光所有的运气，只为了遇见你', '120010020180214', '2b74c61d2ce0e87923220234f3619b7d');
