INSERT INTO `love_letter` VALUES ('1200100201802140078', '匿名', 'W', '一年前跟别人讨论起跟你在一起时各种细节，自己流泪满面。一年后再跟别人说起你，只剩寥寥数语：全是粗口。', '120010020180214', '2b74c61d2ce0e87923220234f3619b7d');
