INSERT INTO `love_letter` VALUES ('1200100201802140080', '匿名', 'LHJ', 'emmm...不过哩，哈哈，我还是匿名的身份，等到寒假结束只要你不嫌弃我，我们就在一起吧！如果你觉得不合适?，我们就试着在一起吧！今天就是情人节了，这里偷偷告个白，我喜欢你！', '120010020180214', '2b74c61d2ce0e87923220234f3619b7d');
INSERT INTO `love_letter` VALUES ('1200100201802140081', '匿名', '男饭二楼“阿叔”', '阿叔，谢谢你，一直以来对我们学生的关照，我相信许多人都曾经在你的窗口打过饭，每当我们学生不开心的时候，你总是用最好的一面来工作，这让我无比感动，快过年了，我不知道这期表白墙是不是过年前最后一期，我想在这里感谢你，希望你过个好年！', '120010020180214', '2b74c61d2ce0e87923220234f3619b7d');
