INSERT INTO `love_letter` VALUES ('1200100201808170040', 'Z', '我朋友叫你YSL', '有些喜欢不知道怎么表达 抱着没多大希望的心情完成这一份表白 一直没联系 也一直不敢找你 偷偷看着你 七夕啦，好久不见！你在你的城市也有悄悄关注我吗 我始终在这里等一个消息我想以后你经过的地方都有我在身边放在心里慢慢发芽', '120010020180817', '2b74c61d2ce0e87923220234f3619b7d');
